import styled from "styled-components";
import { rem } from "polished";
import { spacing } from "../styles/settings/spacing";

export const MainContainer = styled.main`
    max-width: ${rem(991)};
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0 auto;
`;

export default MainContainer;
