import React from "react";
import styled from "styled-components";
import { SPACING } from "./Global Styles/styles";
import SearchBar from "./SearchBar";
import Card from "./Card/Card";

const MainArea = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0px 48px 0px 48px;
`;

const SearchBarArea = styled.div`
    margin: ${SPACING.large} 126px ${SPACING.large} 126px;
`;

const CardWrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    flex-basis: 100%;
    gap: ${SPACING.base};
`;

const Main = ({pokeData}) => {
    return(
        <MainArea>
            <SearchBarArea>
                <SearchBar />
            </SearchBarArea>
            <CardWrapper>
                {pokeData.map((pokemon) => {
                    let pokemonName = pokemonName[0].toUpperCase() + pokemonName.slice(1);
                    return(
                        <Card key={pokemon.name} name={pokemon.name} />
                    );
                })}
            </CardWrapper>
        </MainArea>
    );
}

export default Main;
