import styled from "styled-components";
import { COLORS, SPACING, GRAYSCALE } from "../Global Styles/styles";

export const CardWrapper = styled.div`
    position: relative;
    max-height: 400px;
    max-width: 300px;
    background: ${props => props.type[0].name[0].toUpperCase() + props.type[0].name.slice(1) || "red"};
    border-radius: ${SPACING.small};
    box-shadow: 5px 5px 10px ${GRAYSCALE.lightGray};
    z-index: 1;
`;

export const CardArea = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: ${SPACING.base};
`;

export const CardTitle = styled.div`
    display: flex;
    width: 100%;
`;

export const TitleName = styled.h2`
    flex: 1;
    align-items: flex-start;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

export const TitleNumber = styled.h3`
    display: flex;
    align-items: center;
    align-items: center;
    justify-content: center;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

export const PokemonImage = styled.div`
    border: 0;
    margin-bottom: ${SPACING.xtiny};
`;

export const Image = styled.div`
    justify-content: center;
    align-items: center;
    width: 150px;
    height: 150px;
    background-image: url("001.png");
    background-size: cover;
    z-index: 3;
`;

export const CardInfoArea = styled.div`
    bottom: 5px;
    left: 50%;
    transform: translateX(-50%);
    position: absolute;
    height: 240px;
    width: 290px;
    background: white;
    border-radius: ${SPACING.small};
    z-index: -1;
`;

export const InformationWrapper = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    font-family: 'Poppins', sans-serif;
    margin-top: ${SPACING.mini};
`;

export const InformationHeader = styled.h3`
    justify-content: center;
    align-items: center;
    color: ${COLORS.typeGrass};
`;

export const InformationMeasurements = styled.div`
    display: flex;
    margin-top: ${SPACING.mini};
`;

export const Measurements = styled.div`
    display: flex;
    flex-direction: column;
    padding-top: ${SPACING.small};
`;

export const Measurement = styled.div`
    display: flex;
    justify-content: space-around;
`;

export const MeasurementType = styled.h6`
    font-family: 'Poppins', sans-serif;
    font-weight: normal;
    text-align: center;
    margin-top: ${SPACING.tiny};
    color: ${GRAYSCALE.mediumGray};
`;

export const Divider = styled.hr`
    border: 1px solid ${GRAYSCALE.lightGray};
    height: 48px;
    margin: 0px ${SPACING.base} 0px ${SPACING.base};
`;

export const MeasurementValue = styled.h5`
    font-family: 'Poppins', sans-serif;
    font-weight: normal;
    margin-left: ${SPACING.tiny};
`;

export const Description = styled.h5`
    font-size: 10px;
    line-height: 16px;
    margin-top: ${SPACING.mini};
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
`;

export const TagArea = styled.div`
    display: flex;
    justify-content: space-evenly;
    width: 70%;
`;
