import React from "react";
import styled from "styled-components";
import { COLORS, SPACING } from "./Global Styles/styles";

const CardWrapper = styled.div`
    position: relative;
    min-height: 400px;
    min-width: 300px;
    background: ${COLORS.typeIce};
    border-radius: ${SPACING.small};
`;

const CardArea = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: ${SPACING.base};
`;

const CardTitle = styled.div`
    display: flex;
    width: 100%;
`;

const TitleName = styled.h2`
    flex: 1;
    align-items: flex-start;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

const TitleNumber = styled.h3`
    display: flex;
    align-items: center;
    align-items: center;
    justify-content: center;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

const PokemonImage = styled.div`
    display: flex;
`;

const Image = styled.img`
    justify-content: center;
    align-items: center;
    width: 150px;
    height: 150px;
    background-color: wheat;
`;

const CardInfo = styled.div`
    position: absolute;
    height: 290px;
    width: 240px;
    background: white;
    border-radius: ${SPACING.small};
`;

const Card = () => {
    return(
        <CardWrapper>
            <CardArea>
                <CardTitle>
                    <TitleName>Bulbasaur</TitleName>
                    <TitleNumber>#001</TitleNumber>
                </CardTitle>
                <PokemonImage>
                    <Image/>
                </PokemonImage>
            </CardArea>
            <CardInfo />
        </CardWrapper>
    );
}

export default Card;
