import React from "react";
import styled from "styled-components";
import { spacing } from "../styles/settings/spacing";
import SearchBar from "./SearchBar";
import Card from "./Card/Card";
import { rem } from "polished";

function PokemonList({ pokeData, search, setSearch }) {
    return (
        <>
            <SearchBarArea>
                <SearchBar search={search} setSearch={setSearch} />
            </SearchBarArea>
            <CardWrapper>
                {pokeData.map((pokemon) => {
                    let types = [];
                    pokemon.types.map((type) => {
                        var tipo = type.type.name;
                        tipo = "type" + tipo[0].toUpperCase() + tipo.slice(1);
                        types.push(tipo);
                        return types;
                    });
                    return (
                        <Card key={pokemon.id} name={pokemon.name} id={pokemon.id} image={pokemon.sprites.front_default} types={types} backgroundColor={types[0]}
                            height={pokemon.height} weight={pokemon.weight} />
                    );
                })}
            </CardWrapper>
        </>
    );
}

const MainArea = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0px ${rem(spacing.large)};
`;

const SearchBarArea = styled.div`
    margin: ${rem(spacing.large)} 126px ${rem(spacing.mini)} 126px;
`;

const CardWrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    flex-basis: 100%;
    gap: ${rem(spacing.base)};
    justify-content: space-around;
    margin: ${rem(spacing.medium)};
`;

export default PokemonList;
