import React from "react";
import styled from "styled-components";
import Tag from "./Tag";
import { COLORS, SPACING, GRAYSCALE } from "./Global Styles/styles";

const CardWrapper = styled.div`
    position: relative;
    min-height: 400px;
    min-width: 300px;
    background: ${COLORS.typeGrass};
    border-radius: ${SPACING.small};
    box-shadow: 5px 5px 10px ${GRAYSCALE.lightGray};
    z-index: 1;
`;

const CardArea = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: ${SPACING.base};
`;

const CardTitle = styled.div`
    display: flex;
    width: 100%;
`;

const TitleName = styled.h2`
    flex: 1;
    align-items: flex-start;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

const TitleNumber = styled.h3`
    display: flex;
    align-items: center;
    align-items: center;
    justify-content: center;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

const PokemonImage = styled.div`
    display: flex;
    margin-bottom: ${SPACING.tiny};
    border: none;
`;

const Image = styled.img`
    justify-content: center;
    align-items: center;
    width: 150px;
    height: 150px;
    background-image: url("001.png");
    background-size: cover;
    z-index: 3;
`;

const CardInfo = styled.div`
    bottom: 5px;
    left: 50%;
    transform: translateX(-50%);
    position: absolute;
    height: 240px;
    width: 290px;
    background: white;
    border-radius: ${SPACING.small};
    z-index: -1;
`;

const InformationWrapper = styled.div`
    display: flex;
    justify-content: center;
    font-family: 'Poppins', sans-serif;
`;

const InformationHeader = styled.h3`
    color: ${COLORS.typeGrass};
`;

const Card = () => {
    return(
        <CardWrapper>
            <CardArea>
                <CardTitle>
                    <TitleName>Bulbasaur</TitleName>
                    <TitleNumber>#001</TitleNumber>
                </CardTitle>
                <PokemonImage>
                    <Image/>
                </PokemonImage>
                <Tag type="Grass" color={ COLORS.typeGrass }/>
            </CardArea>
            <InformationWrapper>
                <InformationHeader>About</InformationHeader>
            </InformationWrapper>
            <CardInfo />
        </CardWrapper>
    );
}

export default Card;
