import { createGlobalStyle } from "styled-components";
import styled from "styled-components";
import { GRAYSCALE } from "./components/Global Styles/styles";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Main from "./components/Main";
import { useEffect, useState } from "react";

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;700&display=swap');
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }
`;

const HeaderDivider = styled.hr`
    border: 1px solid ${GRAYSCALE.lightGray};
    box-shadow: 2px 2px 4px rgba(0, 0, 0, 0.25);
`

function App() {
  const [pokeData, setPokeData] = useState([]);
  const [load, setLoad] = useState('true');
  const [search, setSearch] = useState('');

  // Obtener los datos de la API al cargarse la página.
  useEffect(() => {
    const getDataAPI = async () => {
      if(search === ''){
        const toArray = [];
        const url = 'https://pokeapi.co/api/v2/pokemon/?limit=100';
        fetch(url)
        .then(response => response.json())
        .then(data => data.results.map((pokemon) => {
          return void
          fetch(pokemon.url)
          .then(response => response.json())
          .then(data => toArray.push(data));
        }));
        setPokeData(toArray);
      } else {
        const toArray = [];
        const url = 'https://pokeapi.co/api/v2/pokemon/'+search;
        fetch(url)
        .then(response => response.json())
        .then(data => 
          toArray.push(data));
        setPokeData(toArray);
      }
    };
    getDataAPI();
  }, [search]);

  setTimeout(() => {
    console.log(load);
    setLoad(false);
    }, 1000);

  return (
    <div>
      <GlobalStyle />
      <Header />
      <HeaderDivider />
      <Main pokeData={pokeData} search={search} setSearch={setSearch}>
      </Main>
      <Footer />
    </div>
  );
}

export default App;
