import React from "react";
import styled from "styled-components";
import { spacing } from "../styles/settings/spacing";
import SearchBar from "./SearchBar";
import Card from "./Card/Card";

function PokemonList({ pokeData, search, setSearch }) {
    pokeData = pokeData.sort((a, b) => (a.id - b.id) > 0 ? 1 : -1);

    return (
        <MainArea>
            <SearchBarArea>
                <SearchBar search={search} setSearch={setSearch} />
            </SearchBarArea>
            <CardWrapper>
                {pokeData.map((pokemon) => {
                    let pokemonName = pokemon.name[0].toUpperCase() + pokemon.name.slice(1);
                    let types = [];
                    pokemon.types.map((type) => {
                        var tipo = type.type.name;
                        tipo = "type" + tipo[0].toUpperCase() + tipo.slice(1);
                        types.push(tipo);
                        return types;
                    });
                    return (
                        <Card key={pokemon.id} name={pokemonName} id={pokemon.id} image={pokemon.sprites.front_default} types={types} backgroundColor={types[0]}
                            height={pokemon.height} weight={pokemon.weight} />
                    );
                })}
            </CardWrapper>
        </MainArea>
    );
}

const MainArea = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0px 48px 0px 48px;
`;

const SearchBarArea = styled.div`
    margin: ${spacing.large} 126px ${spacing.mini} 126px;
`;

const CardWrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    flex-basis: 100%;
    gap: ${spacing.base};
    justify-content: space-around;
    margin: ${spacing.medium};
`;

export default PokemonList;
