import React from "react";
import styled from "styled-components";
import { SPACING } from "./Global Styles/styles";
import SearchBar from "./SearchBar";
import Card from "./Card/Card";
import { useState, useEffect } from "react/cjs/react.development";

const MainArea = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0px 48px 0px 48px;
`;

const SearchBarArea = styled.div`
    margin: ${SPACING.large} 126px ${SPACING.large} 126px;
`;

const CardWrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    flex-basis: 100%;
    gap: ${SPACING.base};
    justify-content: space-around;
`;

const Main = ({pokeData}) => {
    const [pokemonData, setPokemonData] = useState([]);

    useEffect(() => {
        const pokemons = [];
        pokeData.map((pokemon) => {
            const url = 'https://pokeapi.co/api/v2/pokemon/'+pokemon.name+'/';
            fetch(url)
            .then(response => response.json())
            .then(data => pokemons.push(data));
        });
        setPokemonData(pokemons);
    }, []);

    console.log(pokemonData);
    return(
        <MainArea>
            <SearchBarArea>
                <SearchBar />
            </SearchBarArea>
            <CardWrapper>
                {pokemonData.map((pokemon) => {
                    let pokemonName = pokemon.name[0].toUpperCase() + pokemon.name.slice(1);
                    return(
                        <Card key={pokemonName} name={pokemonName} />
                    );
                })}
            </CardWrapper>
        </MainArea>
    );
}

export default Main;
