import React from "react";
import styled from "styled-components";
import { SPACING } from "./Global Styles/styles";

const TagArea = styled.div`
    display: flex;
    flex: 1;
    order: 1;
    flex-grow: 0;
    width: fit-content;
    padding: ${SPACING.xtiny} ${SPACING.tiny} ${SPACING.xtiny} ${SPACING.tiny};
    border-radius: ${SPACING.mini};
    background: red;
    font-family: 'Poppins', sans-serif;
    color: white;
    font-weight: bold;
`;

const Tag = (props) => {
    return(
        <TagArea>{props.type}</TagArea>
    );
}

export default Tag;
