import styled from "styled-components";
import { rem } from "polished";

export const MainContainer = styled.main`
    max-width: ${rem(1300)};
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0 auto;
`;

export const PageContainer = styled.div`
    max-width: ${rem(1300)};
    display: flex;
    flex: 1;
`;
