import React from "react";
import styled from "styled-components";
import { COLORS, SPACING } from "./Global Styles/styles";

const CardArea = styled.div`
    display: flex;
    flex-direction: column;
    min-height: 400px;
    max-width: 300px;
    border-radius: ${SPACING.small};
    background: ${COLORS.typeGrass};
    padding: ${SPACING.tiny};
`;

const CardTitle = styled.div`
    display: flex;
    margin: ${SPACING.base} ${SPACING.small} 0 ${SPACING.base};
`;

const TitleName = styled.h2`
    flex: 1;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

const TitleNumber = styled.h4`
    display: flex;
    align-items: center;
    justify-content: flex-end;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

const PokemonImage = styled.img`
    max-width: 150px;
    height: auto;
    background-color: wheat;
`;

const CardInfo = styled.div`
    margin-top: 100px;
    height: max-content;
    width: max-content;
    background: white;
    border-radius: ${SPACING.small};
`;

const Card = () => {
    return(
        <CardArea>
            <CardTitle>
                <TitleName>Bulbasaur</TitleName>
                <TitleNumber>#001</TitleNumber>
            </CardTitle>
            <PokemonImage />
            <CardInfo />
        </CardArea>
    );
}

export default Card;
