import React from "react";
import styled from "styled-components";
import { SPACING } from "./Global Styles/styles";
import SearchBar from "./SearchBar";
import Card from "./Card/Card";

const MainArea = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0px 48px 0px 48px;
`;

const SearchBarArea = styled.div`
    margin: ${SPACING.large} 126px ${SPACING.large} 126px;
`;

const CardWrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    flex-basis: 100%;
    gap: ${SPACING.base};
    justify-content: space-around;
`;

const Main = ({pokeData, search, setSearch}) => {
    pokeData = pokeData.sort((a,b) => (a.id - b.id) > 0 ? 1 : -1);
    console.log(pokeData);
    return(
        <MainArea>
            <SearchBarArea>
                <SearchBar search={search} setSearch={setSearch}/>
            </SearchBarArea>
            <CardWrapper>
                {Object.keys(pokeData).length > 1 ? pokeData.map((pokemon) => {
                    let pokemonName = pokemon.name[0].toUpperCase() + pokemon.name.slice(1);
                    let types = [];
                    pokemon.types.map((type) => {
                        var tipo = type.type.name;
                        tipo = "type" + tipo[0].toUpperCase() + tipo.slice(1);
                        types.push(tipo);
                    });
                    return(
                        <Card key={pokemonName} name={pokemonName} id={pokemon.id} image={pokemon.sprites.front_default} types={types} backgroundColor={types[0]}
                        height={pokemon.height} weight={pokemon.weight}/>
                    );
                }) : console.log(pokeData)}
            </CardWrapper>
        </MainArea>
    );
}

export default Main;
