import { createGlobalStyle } from "styled-components";
import styled from "styled-components";
import { GRAYSCALE } from "./components/Global Styles/styles";
import Header from "./components/Header";
import Footer from "./components/Footer";
import SearchBar from "./components/SearchBar";
import Main from "./components/Main";
import { useEffect, useState } from "react";

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;700&display=swap');
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }
`;

const HeaderDivider = styled.hr`
    border: 1px solid ${GRAYSCALE.lightGray};
    box-shadow: 2px 2px 4px rgba(0, 0, 0, 0.25);
`

function App() {
  const [pokeData, setPokeData] = useState([]);
  const [load, setLoad] = useState('true');

  // Obtener los datos de los nombres de los primeros Pokémon
  const getDataAPI = async () => {
    const toArray = [];
    const url = 'https://pokeapi.co/api/v2/pokemon/?limit=21';
    fetch(url)
    .then(response => response.json())
    .then(data => data.results.map((pokemon) => {
      fetch(pokemon.url)
      .then(response => response.json())
      .then(data => toArray.push(data));
    }));
    setPokeData(toArray);
  };

  // Obtener los datos de la API al cargarse la página.
  useEffect(() => {
    getDataAPI();
  }, []);

  setTimeout(() => {
    setLoad(false);
    }, 1000);

  console.log(pokeData);
  return (
    <div>
      <GlobalStyle />
      <Header />
      <HeaderDivider />
      <Main pokeData={pokeData}>
        <SearchBar />
      </Main>
      <Footer />
    </div>
  );
}

export default App;
