import styled from "styled-components";
import { rem } from "polished";

export const MainContainer = styled.main`
    max-width: ${rem(1050)};
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0 auto;
    background-color: rosybrown;
`;

export default MainContainer;
