import React from "react";
import styled from "styled-components";
import { COLORS, SPACING } from "./Global Styles/styles";

const CardArea = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    min-height: 400px;
    min-width: 300px;
    border-radius: ${SPACING.small};
    background: ${COLORS.typeIce};
    padding: ${SPACING.base};
`;

const CardTitle = styled.div`
    display: flex;
    width: fit-content;
    margin: ${SPACING.base} ${SPACING.small} 0 ${SPACING.base};
`;

const TitleName = styled.h2`
    flex: 1;
    align-items: flex-start;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

const TitleNumber = styled.h3`
    align-items: center;
    justify-content: center;
    font-family: 'Poppins', sans-serif;
    color: white;
`;

const PokemonImage = styled.div`
    display: flex;
`;

const Image = styled.img`
    justify-content: center;
    align-items: center;
    width: 150px;
    height: 150px;
    background-color: wheat;
`;

const CardInfo = styled.div`
    margin-top: 100px;
    height: max-content;
    width: max-content;
    background: white;
    border-radius: ${SPACING.small};
`;

const Card = () => {
    return(
        <CardArea>
            <CardTitle>
                <TitleName>Bulbasaur</TitleName>
                <TitleNumber>#001</TitleNumber>
            </CardTitle>
            <PokemonImage>
                <Image/>
            </PokemonImage>
            <CardInfo />
        </CardArea>
    );
}

export default Card;
