import styled from "styled-components";
import { rem } from "polished";

export const MainContainer = styled.main`
    max-width: ${rem(1100)};
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0 auto;
`;

export const HeaderContainer = styled.div`
    max-width: ${rem(1100)};
    background-color: rosybrown;
`;
